$.ajaxSetup({
    headers: {
        'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
    }
});

$.ajax({
    url: 'count_items_index',
    type: 'POST',

    success: function (res) {
        $('#viajes_curso').text(res.viajes)
        $('#empleados').text(res.empleados)
    }
});

$.ajax({
    url: 'chutos_mes',
    type: 'POST',

    success: function (res) {

        var placas = res.placas.replace(/['"]+/g,'').split(',');
        var numViajes = res.numViajes.split(',');
        
        var ctx = document.getElementById("chutosss").getContext('2d');
        new Chart(ctx, {
            type: 'bar',
            data: {
                // el res res.placas trae un string de esta forma: ""044ADA33","0A-DAA0","C455AA","DDD32""
                // ChartJS lo tiene que interpretar es asi "044ADA33","0A-DAA0","C455AA","DDD32"
                // Lo mismo sucede con res.numViajes, retorno un string en lugar de numeros
                // numViajes : "3,2,2,3" -------- Cuando se necesita que sea haci:
                // numViajes : 3,2,2,3
                labels: placas,
                datasets: [{
                    label: '# Chutos más usados en este Mes.',
                    data: numViajes,
                    backgroundColor: [
                        'rgba(255, 99, 132, 0.2)',
                        'rgba(54, 162, 235, 0.2)',
                        'rgba(255, 206, 86, 0.2)',
                        'rgba(75, 192, 192, 0.2)',
                        'rgba(153, 102, 255, 0.2)',
                        'rgba(255, 159, 64, 0.2)',
                        'rgba(255, 99, 132, 0.2)',
                        'rgba(54, 162, 235, 0.2)',
                        'rgba(255, 206, 86, 0.2)',
                        'rgba(75, 192, 192, 0.2)'
                    ],
                    borderColor: [
                        'rgba(255,99,132,1)',
                        'rgba(54, 162, 235, 1)',
                        'rgba(255, 206, 86, 1)',
                        'rgba(75, 192, 192, 1)',
                        'rgba(153, 102, 255, 1)',
                        'rgba(255, 159, 64, 1)',
                        'rgba(255,99,132,1)',
                        'rgba(54, 162, 235, 1)',
                        'rgba(255, 206, 86, 1)',
                        'rgba(75, 192, 192, 1)'
                    ],
                    borderWidth: 1
                }]
            },
            options: {
                scales: {
                    yAxes: [{
                        ticks: {
                            beginAtZero: true
                        }
                    }]
                }
            }
        });
    }
});

